import React from "react";
import { optionsHighlights } from "../options";
import { useEffect, useState } from "react";

const Highlightpopup = ({ dataDva, setDataDva, closeModal }) => {
  const [data, setData] = useState([]);
  const [embed, setEmbedDva] = useState("");

  const loadApi = async () => {
    const fetchData = await fetch(
      "https://free-football-soccer-videos.p.rapidapi.com/",
      optionsHighlights
    );
    const data = await fetchData.json();
    return data;
  };

  const filter = async () => {
    const data = await loadApi();
    const filter = data.filter((item) => item.title === dataDva);
    const dataPrava = filter[0];
    return dataPrava;
  };

  const dataFilter = async () => {
    const data = await filter();
    setData(data);
  };

  useEffect(() => {
    dataFilter();
  }, []);

  function VideoEmbed(props) {
    return <div dangerouslySetInnerHTML={{ __html: props.videoEmbed }} />;
  }

  console.log(data.embed);

  return (
    <div className="bg-black h-full w-full absolute top-0 left-0 z-10">
      <div className="h-">
        <VideoEmbed videoEmbed={data.embed} />
      </div>

      <h1 className="cursor-pointer" onClick={closeModal}>
        Close
      </h1>
    </div>
  );
};

export default Highlightpopup;
