import React from "react";
import { useState, useEffect } from "react";
import { options } from "../options";

const TeamsComp = ({ openClose, league, team }) => {
  const [data, setData] = useState([]);
  const fetchApi = async () => {
    const response = await fetch(
      `https://football98.p.rapidapi.com/${league}/table/squadname/${team}`,
      options
    );
    const json = await response.json();
    const data = await json[0];
    setData(data);
    console.log(data);
  };

  useEffect(() => {
    if (data.length === 0) {
      fetchApi();
    }
  }, []);

  console.log(league);

  return (
    <>
      <div className="w-screen h-[calc(100%-80px)] absolute top-[80px]  z-20 bg-red lg:h-[100%] lg:top-0 lg:w-[calc(screen-250px)]"></div>
    </>
  );
};

export default TeamsComp;
