import React from "react";
import { useState, useEffect } from "react";
import { optionsLive } from "../options";

const Livegames = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchApi = async () => {
    const response = await fetch(
      "https://footapi7.p.rapidapi.com/api/matches/live",
      optionsLive
    );
    const json = await response.json();
    return json;
  };

  const dataSet = async () => {
    setLoading(true);
    const dataDva = await fetchApi();
    const dataTri = Object.values(dataDva);
    setData(dataTri[0]);
    console.log(dataTri[0].awayTeam.name);
    setLoading(false);
  };

  useEffect(() => {
    dataSet();
  }, []);

  return (
    <div className="w-[calc(100%-250px)] h-full  absolute top-0 left-[250px] flex-col  items-center">
      {data.map((item) => {
        <div className="text-black">
          <h1>{item.awayTeam.name}</h1>
        </div>;
      })}
    </div>
  );
};

export default Livegames;
