import React from "react";
import { options } from "../options";
import { useState, useEffect } from "react";

const Transfercard = ({ league, openModal }) => {
  const [data, setData] = useState([]);
  const fetchApi = async () => {
    const response = await fetch(
      `https://football98.p.rapidapi.com/${league}/transfers`,
      options
    );
    const json = await response.json();
    const data = await json[0];
    const newData = Object.values(json);
    setData(newData);
    console.log(data.playerName);
  };

  useEffect(() => {
    if (data.length === 0) {
      fetchApi();
    }
  }, []);
  return (
    <div className="flex justify-center items-center h-[100%] w-full absolute top-0 bg-red "></div>
  );
};

export default Transfercard;
