import React from "react";
import { useState, useEffect } from "react";
import { optionsLive } from "../options";

const Livegamesdetails = ({ openClose }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchApi = async () => {
    const response = await fetch(
      "https://footapi7.p.rapidapi.com/api/matches/live",
      optionsLive
    );
    const json = await response.json();
    return json;
  };

  const dataSet = async () => {
    setLoading(true);
    const dataDva = await fetchApi();
    const dataTri = Object.values(dataDva);

    console.log(dataTri[0]);
    if (dataTri[0].length > 9) {
      const newData = dataTri[0].slice(0, 9);
      setData(newData);
    }
    setData(dataTri[0]);

    setLoading(false);
  };

  useEffect(() => {
    if (data.length === 0) {
      dataSet().then(() => console.log(data));
    }
  }, [data]);
  return (
    <div className="w-full h-full bg-black bg-opacity-80 absolute top-0 flex justify-center items-center">
      <div className="w-[50%] h-[70%] bg-white border-8 border-black-400">
        <button
          onClick={openClose}
          className="bg-yellow w-[50px] h-[30px] hover:bg-red hover:w-[100px] hover:text-white transition-all"
        >
          Close
        </button>
      </div>
    </div>
  );
};

export default Livegamesdetails;
