import React from "react";
import { options } from "../options";
import { useState, useEffect } from "react";

const Transfercard = ({ league, openModal }) => {
  const [data, setData] = useState([]);
  const fetchApi = async () => {
    const response = await fetch(
      `https://football98.p.rapidapi.com/${league}/transfers`,
      options
    );
    const json = await response.json();
    const data = await json[0];
    const newData = Object.values(json);
    setData(newData);
    console.log(newData);
  };

  useEffect(() => {
    if (data.length === 0) {
      fetchApi();
    }
  }, []);
  return (
    <div className="w-[800px] h-[800px] bg-yellow absolute">
      <div className="h-[80%] w-full grid grid-cols-2">
        {data.map((item) => {
          <div>
            <h1>{item.newClub}</h1>{" "}
          </div>;
        })}
      </div>
      <button onClick={openModal} className="bg-black text-white">
        Close
      </button>
    </div>
  );
};

export default Transfercard;
