import React from "react";
import { useState, useEffect } from "react";
import { optionsLive } from "../options";
import Livegamesdetails from "../components/Livegamesdetails";
import img1 from "../slike/livegames.jpg";

const Livegames = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [visible, setVisible] = useState(false);
  const [name, setName] = useState("");
  const [object, setObject] = useState({});
  const [currentPage, setCurrentPage] = useState(1);

  const gamesPerPage = 10;

  const handleRightArrow = () => {
    setCurrentPage(currentPage + 1);
    console.log(currentPage);
  };

  const handleLeftArrow = () => {
    setCurrentPage(currentPage - 1);
  };

  const styles = {
    backgroundImage: `url(${img1})`,
  };

  const handleObject = (object) => setObject(object);

  const openClose = () => setVisible((prev) => !prev);

  const fetchApi = async () => {
    const response = await fetch(
      "https://footapi7.p.rapidapi.com/api/matches/live",
      optionsLive
    );
    const json = await response.json();
    return json;
  };

  const dataSet = async () => {
    setLoading(true);
    const dataDva = await fetchApi();
    const dataTri = Object.values(dataDva);

    console.log(dataTri[0]);

    const startIndex = (currentPage - 1) * gamesPerPage;
    const endIndex = currentPage * gamesPerPage;
    const newData = dataTri[0].slice(startIndex, endIndex);
    setData(newData);

    setLoading(false);
  };

  useEffect(() => {
    dataSet();
  }, []);

  return (
    <div className="w-screen h-screen flex-col  items-center">
      <div className="absolute h-full w-[calc(100%-250px)] z-[-1] bg-black bg-opacity-60 "></div>
      <div
        className="absolute h-full w-[calc(100%-250px)] bg-cover z-[-2] bg-black bg-opacity-80 "
        style={styles}
      ></div>
      <button
        onClick={() => {
          handleRightArrow();
          console.log(currentPage);
        }}
      >
        Right
      </button>
      {visible && (
        <Livegamesdetails
          openClose={openClose}
          data={data}
          name={name}
          object={object}
        />
      )}
      <div className="h-full w-full">
        <div className="centar">
          {data.length === 0 ? (
            <h1>Couldn't access server info, try again tomorrow</h1>
          ) : (
            data.map((item) => {
              return (
                <div
                  onClick={() => {
                    handleObject(item);
                    openClose();
                  }}
                  className="mt-[21px] hover:bg-yellow cursor-pointer hover:text-white transition-all text-black shadow-md bg-grey pl-8 pr-8 mt-4 pt-4 pb-4 flex grid grid-cols-3 w-[80%] "
                >
                  <div className="flex items-center">
                    <h1 className="">{item.homeTeam.name}</h1>{" "}
                  </div>
                  <div className="flex justify-center pb-2 pt-2 bg-yellow shadow-xl">
                    {item.homeScore.display}:{item.awayScore.display}{" "}
                  </div>
                  <div className="flex justify-end items-center">
                    <h1>{item.awayTeam.name}</h1>{" "}
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
};

export default Livegames;
