import React from "react";
import { useState, useEffect } from "react";
import { optionsLive } from "../options";

const Livegamesdetails = ({ openClose, data, name, object }) => {
  console.log(object);
  return (
    <div className="w-full h-full bg-black bg-opacity-80 absolute top-0 flex justify-center items-center">
      <div className="w-[50%] h-[70%] bg-white border-8 border-black-400">
        <button
          onClick={openClose}
          className="bg-yellow w-[50px] h-[5%] hover:bg-red hover:w-[100px] hover:text-white transition-all"
        >
          Close
        </button>
        <div className="flex justify-center h-[5%] mt-10 mb-4">
          <h1>{object.tournament.name}</h1>
        </div>
        <div className="w-full h-[30%] bg-yellow mt-10 grid grid-cols-2 pt-6 rounded-xl shadow-xl">
          <div className="">
            {" "}
            <h1 className="text-4xl flex justify-center">
              {object.homeTeam.name}
            </h1>
            <h1 className="text-6xl font-bold flex justify-center mt-5">
              {object.homeScore.current}
            </h1>
            <div></div>
          </div>
          <div>
            {" "}
            <h1 className="text-4xl flex justify-center">
              {object.awayTeam.name}
            </h1>
            <h1 className="text-6xl font-bold flex justify-center mt-5">
              {object.awayScore.current}
            </h1>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Livegamesdetails;
