import React from "react";
import { useState, useEffect } from "react";
import { options } from "../options";

const TeamsComp = ({ openClose, league, team }) => {
  const [data, setData] = useState([]);
  const fetchApi = async () => {
    const response = await fetch(
      `https://football98.p.rapidapi.com/${league}/table/squadname/${team}`,
      options
    );
    const json = await response.json();

    return json;
  };

  const handleData = async () => {
    const data = await fetchApi();
    setData(data);
    console.log(data);
  };

  useEffect(() => {
    handleData();
  }, []);

  console.log(league);

  return (
    <div className="w-full h-full  bg-black absolute top-0">
      <button className="bg-yellow" onClick={openClose}>
        Close
      </button>
    </div>
  );
};

export default TeamsComp;
