import React from "react";
import { useEffect, useState } from "react";
import { options } from "../options";

const Lastmatcheng = () => {
  const [data, setData] = useState([]);
  const loadApi = async () => {
    const response = await fetch(
      "https://football98.p.rapidapi.com/premierleague/results",
      options
    );
    const data = await response.json();
    return data;
  };

  const constMaker = async () => {
    const data = await loadApi();
    const dataArray = Object.values(data[0]);
    const zadnjaData = dataArray[0];
    return zadnjaData;
  };

  const consoleLog = async () => {
    const log = await constMaker();
    setData((prevstate) => log);
    console.log(data);
  };

  useEffect(() => {
    consoleLog();
  }, []);

  return (
    <div className="text-white shadow-xl top-0 w-[500px] h-[450px] bg-white border-8 border-black-400">
      <div className="w-full h-[10%] flex justify-center text-black items-center bg-yellow">
        <h1>Lastest Premier League Matches</h1>
      </div>
      {data.length > 1 ? (
        ""
      ) : (
        <div className="w-full h-[80%] flex justify-center items-center">
          <h1 className="text-black text-4xl font-bold">Loading...</h1>{" "}
        </div>
      )}
      {data.map((item) => {
        return (
          <div className="w-full h-[7%] text-black flex justify-center mt-2">
            <img src={item.homeLogo} className="h-[25px] pr-4" />
            <h1>
              {item.homeTeam} {item.homeTeamScore}:{item.awayTeamScore}{" "}
              {item.awayTeam}
            </h1>
            <img src={item.awayLogo} className="h-[25px] pl-4" />
          </div>
        );
      })}
    </div>
  );
};

export default Lastmatcheng;
