import React from "react";
import { NavLink, Link } from "react-router-dom";
import img from "../slike/logo.png";
import "boxicons";
import { useState } from "react";

const Navbar = () => {
  const [className, setClassName] = useState(
    "h-[80%] opacity-0 absolute top-[80px] "
  );

  const handleClick = () => {
    setClassName((prev) => prev + "opacity-100");

    console.log(className);
  };

  console.log(className);
  return (
    <div className="h-[80px] top-0 left-0 w-screen absolute bg-yellow text-black lg:relative lg:w-[250px] lg:h-screen">
      <div className="h-[20%] flex justify-center items-center">
        <Link to="/">
          <img
            src={img}
            className="mt-12 shadow-xl cursor-pointer w-[100px] lg:w-auto lg:mt-8"
          />
        </Link>
      </div>
      <div
        className="absolute top-0 h-full flex items-center pb-3 z-10"
        onClick={handleClick}
      >
        <box-icon
          name="menu-alt-left"
          className="w-[100px]"
          size="lg"
        ></box-icon>
      </div>

      <div className={className}>
        <li className="block text-xl  bg-yellow shadow-lg transition-all hover:bg-white hover:text-black">
          <NavLink to="/" className=" pl-4 ">
            Leagues
          </NavLink>
        </li>
        <li className="block  mt-1 text-xl bg-yellow shadow-lg transition-all hover:bg-white hover:text-black">
          <NavLink to="/teams" className="pl-4">
            Teams
          </NavLink>
        </li>
        <li className="block  mt-1 text-xl  bg-yellow shadow-lg transition-all hover:bg-white hover:text-black">
          <NavLink to="/transfers" className="pl-4 ">
            Transfers
          </NavLink>
        </li>
        <li className="block  mt-1 text-xl  bg-yellow shadow-lg transition-all hover:bg-white hover:text-black">
          <NavLink to="/livegames" className="pl-4 ">
            Live games
          </NavLink>
        </li>{" "}
      </div>
    </div>
  );
};

export default Navbar;
