import React from "react";
import { useState, useEffect } from "react";
import { options } from "../options";

const TeamsComp = ({ handleTeams, title, team }) => {
  const [data, setData] = useState([]);
  const fetchApi = async () => {
    const response = await fetch(
      `https://football98.p.rapidapi.com/${title}/table/squadname/${team}`,
      options
    );
    const json = await response.json();
    const data = await json[0];
    setData(data);
    console.log(data);
  };

  useEffect(() => {
    if (data.length === 0) {
      fetchApi();
    }
  }, []);
  console.log(title, team);

  return (
    <>
      <div className="w-full h-full  bg-black absolute top-0 ">
        <div className=" h-full w-[80] bg-white">
          <img src={data.SquadLogo}></img>
          <div className="text-black">
            <h1>Team Name: {data.Name}</h1>
            <h1>Points in league 2022/2023: {data.Points}</h1>
            <h1>Games played in league 2022/2023: {data.Played}</h1>
            <h1>Games won 2022/2023: {data.Winned}</h1>
            <h1>Games lost 2022/2023: {data.Loosed}</h1>
            <h1>Games tied 2022/2023: {data.Tie}</h1>
          </div>
        </div>
        <button className="bg-yellow" onClick={handleTeams}>
          Close
        </button>
      </div>
    </>
  );
};

export default TeamsComp;
