import React from "react";
import { useState, useEffect } from "react";
import { optionsLive } from "../options";

const Livegames = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchApi = async () => {
    const response = await fetch(
      "https://footapi7.p.rapidapi.com/api/matches/live",
      optionsLive
    );
    const json = await response.json();
    return json;
  };

  const dataSet = async () => {
    setLoading(true);
    const dataDva = await fetchApi();
    const dataTri = Object.values(dataDva);
    setData(dataTri[0]);
    console.log(dataTri[0]);

    setLoading(false);
  };

  useEffect(() => {
    if (data.length === 0) {
      dataSet().then(() => console.log(data));
    }
  }, []);

  return (
    <div className="w-[calc(100%-250px)] h-full  absolute top-0 left-[250px] flex-col  items-center">
      <div className="centar">
        {data.map((item) => {
          return (
            <div className="hover:bg-yellow hover:text-white transition-all text-black shadow-md bg-grey pl-8 pr-8 mt-4 pt-4 pb-4 flex grid grid-cols-3 w-[80%] border-4 border-grey">
              <div className="flex items-center">
                <h1 className="text-black">{item.homeTeam.name}</h1>{" "}
              </div>
              <div className="flex justify-center pb-2 pt-2 bg-yellow shadow-xl">
                {item.homeScore.display}:{item.awayScore.display}{" "}
              </div>
              <div className="flex justify-end items-center">
                <h1>{item.awayTeam.name}</h1>{" "}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Livegames;
