import React from "react";
import { useState } from "react";
import TeamsComp from "../League components/TeamsComp";
import img1 from "../slike/teams.jpg";

const Teams = () => {
  const [league, setLeague] = useState("premierleague");
  const [team, setTeam] = useState("");
  const [visible, setVisible] = useState(false);

  const styles = {
    backgroundImage: `url(${img1})`,
  };

  const openModal = () => setVisible((prev) => !prev);
  const ifModal = () => {
    if ((league, team === "")) {
      alert("You must select league and enter a teams name");
    } else {
      openModal();
    }
  };

  console.log(league, team);
  return (
    <div className="w-screen h-screen ">
      <div className="absolute h-full w-full z-[-1] bg-black bg-opacity-60 lg:w-[calc(screen-250px)]"></div>
      <div
        className="absolute h-full w-full bg-cover z-[-2] bg-black bg-opacity-80 lg:w-[calc(screen-250px)] "
        style={styles}
      ></div>
    </div>
  );
};

export default Teams;
