import React from "react";
import { optionsHighlights } from "../options";
import { useEffect, useState } from "react";
import { compareTwoStrings } from "string-similarity";

const Highlightpopup = ({ dataDva, setDataDva, closeModal }) => {
  const [data, setData] = useState([]);
  const [embed, setEmbedDva] = useState("");
  const [url, setUrl] = useState("");

  const loadApi = async () => {
    const fetchData = await fetch(
      "https://free-football-soccer-videos.p.rapidapi.com/",
      optionsHighlights
    );
    const data = await fetchData.json();
    return data;
  };

  const filter = async () => {
    const data = await loadApi();
    const filter = data.filter(
      (item) => compareTwoStrings(item.title, dataDva) > 0.5
    );

    const dataPrava = filter[0];

    return dataPrava;
  };

  const dataFilter = async () => {
    const data = await filter();
    setData(data);
    const embeded = data.embed;

    let startIndex = embeded.indexOf("src='") + 5;
    let endIndex = embeded.indexOf("'", startIndex);
    let src = embeded.substring(startIndex, endIndex);
    setUrl(src);
  };

  useEffect(() => {
    dataFilter();
  }, []);

  console.log(data);

  return (
    <div className="bg-black h-full w-full absolute top-0 left-0 z-10 ">
      {data === undefined ? (
        <div className="h-full w-full flex justify-center items-center">
          <h1>No highlight has been found for selected match.</h1>{" "}
        </div>
      ) : (
        <div className="h-full">
          <iframe src={url} className="h-full w-full"></iframe>
        </div>
      )}

      <h1
        className="cursor-pointer bg-white text-black absolute"
        onClick={closeModal}
      >
        Close
      </h1>
    </div>
  );
};

export default Highlightpopup;
