import React from "react";
import { useState, useEffect } from "react";
import { options } from "../options";

const Lastmatcheng = () => {
  const [data, setData] = useState([]);
  const loadApi = async () => {
    const response = await fetch(
      "https://football98.p.rapidapi.com/premierleague/fixtures",
      options
    );
    const data = await response.json();
    return data;
  };

  const constMaker = async () => {
    const data = await loadApi();
    const dataArray = Object.values(data[0]);
    const zadnjaData = dataArray[0];
    return zadnjaData;
  };

  const consoleLog = async () => {
    const log = await constMaker();
    setData((prevstate) => log);
    console.log(data);
  };

  useEffect(() => {
    consoleLog();
  }, []);

  return (
    <div className="ml-[70px] text-white shadow-xl top-0 w-[500px] h-[450px] bg-white border-8 border-black-400">
      <div className="w-full h-[10%] flex justify-center text-black items-center bg-yellow">
        <h1>Next Premier League Matches</h1>
      </div>
      {data.length > 1 ? (
        ""
      ) : (
        <div className="w-full h-[80%] flex justify-center items-center">
          <h1 className="text-black text-4xl font-bold">Loading...</h1>{" "}
        </div>
      )}
      {data.map((item) => {
        return (
          <div className="w-full h-[7%] text-black flex justify-center mt-2 grid grid-cols-3">
            <div className="flex ">
              <img src={item.homeLogo} className="h-[25px] pr-4" />{" "}
              <h1>{item.homeTeam}</h1>
            </div>
            <div className="flex bg-yellow justify-center shadow-xl">
              <h1>vs</h1>{" "}
            </div>
            <div className="flex justify-end">
              <h1>{item.awayTeam}</h1>
              <img src={item.awayLogo} className="h-[25px] pl-4" />{" "}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Lastmatcheng;
