import React from "react";
import { NavLink, Link } from "react-router-dom";
import img from "../slike/logo.png";
import "boxicons";
import { useState } from "react";

const Navbar = () => {
  const [className, setClassName] = useState(
    "h-[80%] w-full hidden absolute top-[80px] z-10 lg:opacity-100 lg:relative lg:top-0 lg:block "
  );

  const handleClick = () => {
    if (!className.includes("hidden")) {
      setClassName((prev) => prev + "hidden");
    } else {
      setClassName(className.replace("hidden", ""));
    }

    console.log(className);
  };

  console.log(className);
  return (
    <div className="h-[80px] top-0 w-[100%] absolute bg-yellow text-black lg:relative lg:w-[250px] lg:h-screen"></div>
  );
};

export default Navbar;
