import React from "react";
import { useState } from "react";
import TeamsComp from "../League components/TeamsComp";
import img1 from "../slike/teams.jpg";

const Teams = () => {
  const [league, setLeague] = useState("premierleague");
  const [team, setTeam] = useState("");
  const [visible, setVisible] = useState(false);

  const styles = {
    backgroundImage: `url(${img1})`,
  };

  const openModal = () => setVisible((prev) => !prev);
  const ifModal = () => {
    if ((league, team === "")) {
      alert("You must select league and enter a teams name");
    } else {
      openModal();
    }
  };

  console.log(league, team);
  return (
    <div className="w-screen h-screen ">
      <div className="absolute h-full w-full z-[-1] bg-black bg-opacity-60 "></div>
      <div
        className="absolute h-full w-full bg-cover z-[-2] bg-black bg-opacity-80 "
        style={styles}
      ></div>

      {visible && (
        <TeamsComp openClose={openModal} league={league} team={team} />
      )}
      <div className="klasa">
        <h1 className="text-4xl mb-4 text-white">
          Please select league and enter teams name{" "}
        </h1>
        <div className="flex flex-col">
          <div className="flex ">
            <input
              onChange={(event) => setTeam(event.target.value)}
              className="bg-yellow  w-[300px] h-[50px] outline-0"
            ></input>
            <select
              className="bg-yellow flex"
              onClick={(event) => setLeague(event.target.value)}
            >
              <option value="premierleague" className="h-[10px]">
                Premier League
              </option>
              <option value="laliga" className="h-[10px]">
                LaLiga
              </option>
              <option value="bundesliga" className="h-[10px]">
                Austrian Bundesliga
              </option>
              <option value="seriea" className="h-[10px]">
                Serie A
              </option>
              <option value="ligue1ubereats" className="h-[10px]">
                Ligue 1
              </option>
              <option value="russianpremierleague" className="h-[10px]">
                Russian Premier League
              </option>
              <option value="ligaportugal" className="h-[10px]">
                Liga Portugal
              </option>
              <option value="eredivisie" className="h-[10px]">
                Eredivisie
              </option>
              <option value="jupilerproleague" className="h-[10px]">
                Jupiler pro league
              </option>
              <option value="brasileirãosériea" className="h-[10px]">
                Brasileirao
              </option>
              <option value="majorleaguesoccer" className="h-[10px]">
                Mls
              </option>
              <option value="primeradivisiónrfef" className="h-[10px]">
                Liga MX
              </option>
              <option value="scottishleagueone" className="h-[10px]">
                SPFL
              </option>
              <option value="2.bundesliga" className="h-[10px]">
                2.Bundesliga
              </option>
              <option value="1.hnl" className="h-[10px]">
                1.Hnl
              </option>
            </select>
          </div>
          <div className="flex justify-center mt-6">
            <button
              className="bg-yellow w-[150px] h-[50px] rounded-md hover:bg-red hover:text-white transition-all"
              onClick={ifModal}
            >
              Search
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Teams;
