import React from "react";
import { options } from "../options";
import { useState, useEffect } from "react";

const Transfercard = ({ league, openModal }) => {
  const [data, setData] = useState([]);
  const fetchApi = async () => {
    const response = await fetch(
      `https://football98.p.rapidapi.com/${league}/transfers`,
      options
    );
    const json = await response.json();
    const data = await json[0];
    const newData = Object.values(json);
    setData(newData);
    console.log(data.playerName);
  };

  useEffect(() => {
    if (data.length === 0) {
      fetchApi();
    }
  }, []);
  return (
    <div className="w-[800px] h-[800px] bg-white absolute">
      {" "}
      <button onClick={openModal} className="bg-black text-white">
        Close
      </button>
      <div className="h-[90%] w-full grid grid-cols-3">
        {data.map((item) => {
          return (
            <div className="klasadva">
              <h1>
                from {item.oldClub} to {item.newClub}
              </h1>{" "}
              <h1 className="text-xl">Price paid: {item.price}</h1>
              <h1 className="text-2xl">{item.playerName}</h1>
              <h1>{item.playerRole}</h1>
              <h1>{item.transferDate}</h1>
              <h1>{item.transferType}</h1>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Transfercard;
