import React from "react";
import { NavLink, Link } from "react-router-dom";
import img from "../slike/logo.png";
import "boxicons";
import { useState } from "react";

const Navbar = () => {
  const [className, setClassName] = useState(
    "h-[80%] w-full hidden absolute top-[80px] z-10 lg:opacity-100 lg:relative lg:top-0 lg:block "
  );

  const handleClick = () => {
    if (!className.includes("hidden")) {
      setClassName((prev) => prev + "hidden");
    } else {
      setClassName(className.replace("hidden", ""));
    }

    console.log(className);
  };

  console.log(className);
  return (
    <div className="h-[80px] top-0 w-[100%] absolute bg-yellow text-black lg:relative lg:w-[250px] lg:h-screen">
      <div className="h-full w-full  flex justify-center items-center lg:h-[25%] lg:mb-5 2xl:h-[20%] 2xl:mb-2 ">
        <Link to="/">
          <img
            src={img}
            className=" shadow-xl cursor-pointer w-[100px] lg:w-auto lg:mt-8"
          />
        </Link>
      </div>
      <div
        className="absolute  top-0 h-full flex items-center pb-3 z-10 lg:opacity-0 lg:absolute lg:h-[5%]"
        onClick={handleClick}
      >
        <box-icon
          name="menu-alt-left"
          className="w-[100px]"
          size="lg"
        ></box-icon>
      </div>
    </div>
  );
};

export default Navbar;
