import React from "react";
import { useState, useEffect } from "react";
import { options } from "../options";

const TeamsComp = ({ openClose, league, team }) => {
  const [data, setData] = useState([]);
  const fetchApi = async () => {
    const response = await fetch(
      `https://football98.p.rapidapi.com/${league}/table/squadname/${team}`,
      options
    );
    const json = await response.json();
    const data = await json[0];
    setData(data);
    console.log(data);
  };

  useEffect(() => {
    if (data.length === 0) {
      fetchApi();
    }
  }, []);

  console.log(league);

  return (
    <div className="w-full h-full  bg-black absolute top-0 ">
      <div className=" h-[80%] w-[80] bg-white">
        <img src={data.SquadLogo}></img>
        <div className="text-black">
          <h1>Team Name: {data.Name}</h1>
        </div>
      </div>
      <button className="bg-yellow" onClick={openClose}>
        Close
      </button>
    </div>
  );
};

export default TeamsComp;
