import React from "react";
import { useState } from "react";
import TeamsComp from "../League components/TeamsComp";
import img1 from "../slike/teams.jpg";

const Teams = () => {
  const [league, setLeague] = useState("premierleague");
  const [team, setTeam] = useState("");
  const [visible, setVisible] = useState(false);

  const styles = {
    backgroundImage: `url(${img1})`,
  };

  const openModal = () => setVisible((prev) => !prev);
  const ifModal = () => {
    if ((league, team === "")) {
      alert("You must select league and enter a teams name");
    } else {
      openModal();
    }
  };

  console.log(league, team);
  return (
    <div className="w-screen h-screen ">
      <div className="absolute h-full w-full z-[-1] bg-black bg-opacity-60 "></div>
      <div
        className="absolute h-full w-full bg-cover z-[-2] bg-black bg-opacity-80 "
        style={styles}
      ></div>

      {visible && (
        <TeamsComp openClose={openModal} league={league} team={team} />
      )}
      <div className="klasa">
        <h1 className="text-6xl mb-10 text-center text-white">
          Please select league and enter teams name{" "}
        </h1>
        <div className="flex flex-col md:flex-row">
          <input
            onChange={(event) => setTeam(event.target.value)}
            className="bg-yellow  w-[300px] h-[50px] outline-0 text-center"
          ></input>
          <select
            size={1}
            className="bg-yellow flex h-[50px] text-center"
            onClick={(event) => setLeague(event.target.value)}
          >
            <option value="premierleague">Premier League</option>
            <option value="laliga">LaLiga</option>
            <option value="bundesliga">Austrian Bundesliga</option>
            <option value="seriea">Serie A</option>
            <option value="ligue1ubereats">Ligue 1</option>
            <option value="russianpremierleague">Russian Premier League</option>
            <option value="ligaportugal">Liga Portugal</option>
            <option value="eredivisie">Eredivisie</option>
            <option value="jupilerproleague">Jupiler pro league</option>
            <option value="brasileirãosériea">Brasileirao</option>
            <option value="majorleaguesoccer">Mls</option>
            <option value="primeradivisiónrfef">Liga MX</option>
            <option value="scottishleagueone">SPFL</option>
            <option value="2.bundesliga">2.Bundesliga</option>
            <option value="1.hnl">1.Hnl</option>
          </select>

          <div className="flex justify-center mt-10">
            <button
              className="bg-yellow w-[150px] h-[50px] rounded-md hover:bg-red hover:text-white transition-all"
              onClick={ifModal}
            >
              Search
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Teams;
