import React from "react";
import { optionsHighlights } from "../options";
import { useEffect, useState } from "react";

const Highlightpopup = ({ dataDva, setDataDva, closeModal }) => {
  const [data, setData] = useState([]);
  const [embedDva, setEmbedDva] = useState("");

  const filterVideo = data.filter((item) => item.title === dataDva);

  const { embed, url } = filterVideo;

  console.log(filterVideo);

  useEffect(() => {
    fetch(
      "https://free-football-soccer-videos.p.rapidapi.com/",
      optionsHighlights
    )
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((err) => console.error(err));
  }, []);
  console.log(url);
  return (
    <div className="bg-black h-full w-full absolute top-0 left-0 z-10">
      <video className="w- [500px] h-[500px]" controls>
        <source src={filterVideo.url} />
      </video>
      <h1 className="cursor-pointer" onClick={closeModal}>
        Highlightpopup
      </h1>
    </div>
  );
};

export default Highlightpopup;
